import React from "react";
import "./Select.css"; // Importing the CSS for this page

const Select = () => {
  // Example data for popular goals
  const goals = [
    { id: 1, name: "UPSC CSE - GS", icon: "üèõÔ∏è" },
    { id: 2, name: "IIT JEE", icon: "‚öóÔ∏è" },
    { id: 3, name: "NEET UG", icon: "ü©∫" },
    { id: 4, name: "Bank exams", icon: "üí∞" },
    { id: 5, name: "SSC JE & state AE exams", icon: "üìã" },
    { id: 6, name: "CAT & other MBA entrance tests", icon: "‚úèÔ∏è" },
    { id: 7, name: "CBSE class 12", icon: "üìñ" },
    { id: 8, name: "CA Intermediate", icon: "üìä" },
    {}
  ];

  return (
    <div className="select-container">
      <h1 className="main-heading">Select your goal / exam</h1>
      <p className="sub-heading">200+ exams available for your preparation</p>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Type the goal / exam you're preparing for"
          className="search-input"
        />
      </div>

      <h2 className="popular-goals-heading">Popular goals</h2>
      <div className="goals-grid">
        {goals.map((goal) => (
          <div key={goal.id} className="goal-card">
            <div className="goal-icon">{goal.icon}</div>
            <p className="goal-name">{goal.name}</p>
          </div>
        ))}
      </div>

      <button className="see-all-button">See all goals (200+)</button>
    </div>
  );
};

export default Select;
